import React, { useState } from 'react';
import { Link } from 'react-router-dom';


function Card({ Cards, categorySelected }) {

    const filtro = {
        recente: 'Recente',
        antigo: 'Antigo'
    }

    const [controlFilter, setControlFilter] = useState(filtro.recente)


    return (
        <div className="container">
            <div id="cards">
                <div id="organizar-card">
                    <label>Ordenar</label>
                    <select onChange={(e) => setControlFilter(e.target.value)}>
                        <option value={filtro.recente}>{filtro.recente}</option>
                        <option value={filtro.antigo}>{filtro.antigo}</option>
                    </select>
                </div>

                <div id="exibir-cards">
                    {Cards.map((card) => (
                        <div className="card">
                            <div>
                                <p>{card.Icon}</p>
                                <p>{card.Title}</p>
                            </div>
                            <div className="card-footer">
                                <p></p>
                                <Link to={`/Saiba-mais/${card.Title}`}>Saiba mais</Link>
                            </div>
                        </div>
                    ))}
                </div>

            </div>
        </div>
    )
}

export default Card;
